{
    "tiddlers": {
        "$:/plugins/wikilabs/thin-scrollbars/history": {
            "title": "$:/plugins/wikilabs/thin-scrollbars/history",
            "text": "V 0.1.0 - 2021-01-05

* initial release
"
        },
        "$:/plugins/wikilabs/thin-scrollbars/license": {
            "title": "$:/plugins/wikilabs/thin-scrollbars/license",
            "text": "[[Thin Scrollbars CSS|https://wikilabs.github.io/#thin-scrollbars]] (c) Mario Pietsch - 2021-

https://opensource.org/licenses/BSD-3-Clause
"
        },
        "$:/plugins/wikilabs/thin-scrollbars/readme": {
            "title": "$:/plugins/wikilabs/thin-scrollbars/readme",
            "text": "This plugin adds a little bit of CSS that creates small scrollbars for ~FireFox and ~WebKit based browsers
"
        },
        "$:/plugins/wikilabs/thin-scrollbars/styles": {
            "title": "$:/plugins/wikilabs/thin-scrollbars/styles",
            "tags": "$:/tags/Stylesheet",
            "type": "text/css",
            "text": "* {
	scrollbar-width: thin;
}

.tc-tab-content:hover,
.tc-sidebar-scrollable:hover {
	scrollbar-width: auto;
}

*::-webkit-scrollbar {
	width: .4em;
	height: .4em;
}

*::-webkit-scrollbar-thumb {
	background: #bbb;
}

.tc-tab-content:hover::-webkit-scrollbar,
.tc-sidebar-scrollable:hover::-webkit-scrollbar {
	width: .8em;
	height: .8em;
}"
        }
    }
}
