<$vars targetTiddler="""$tiddler$""" targetField="""$field$""">
  <$type$ class="$class$">
    <$list filter="[subfilter{$tiddler$!!$field$}]">
    <$subtype$ class="sub-$class$">
        <$transclude tiddler="""$itemTemplate$""">
          <$link to={{!!title}}>
            <$transclude field="caption"><$view field="title" /></$transclude>
          </$link>
        </$transclude>
    </$subtype$>
    </$list>
  </$type$>
</$vars>
\end

