{
    "tiddlers": {
        "$:/core/ui/DefaultSearchResultList": {
            "title": "$:/core/ui/DefaultSearchResultList",
            "tags": "$:/tags/SearchResults",
            "caption": "{{$:/language/Search/DefaultResults/Caption}}",
            "text": "\define searchResultList()
//<small>{{$:/language/Search/Matches/Title}}</small>//

<$list filter="[!is[system]search:title{$(searchTiddler)$}sort[title]limit[250]]" template="$:/core/ui/ListItemTemplate" emptyMessage={{$:/plugins/wikilabs/new-tiddler-from-search/template}}/>

//<small>{{$:/language/Search/Matches/All}}</small>//

<$list filter="[!is[system]search{$(searchTiddler)$}sort[title]limit[250]]" template="$:/core/ui/ListItemTemplate"/>

\end
<<searchResultList>>
"
        },
        "$:/plugins/wikilabs/new-tiddler-from-search/styles": {
            "title": "$:/plugins/wikilabs/new-tiddler-from-search/styles",
            "tags": "$:/tags/Stylesheet",
            "text": "
.wikilabs-full-width{
	width: 100%;
}

"
        },
        "$:/plugins/wikilabs/new-tiddler-from-search/template": {
            "title": "$:/plugins/wikilabs/new-tiddler-from-search/template",
            "text": "\define createTiddler()
<$action-setfield $tiddler={{$(searchTiddler)$!!text}}
  tags= {{$(new-tiddler-temp)$!!tags}}
  text= {{$(new-tiddler-temp)$}}
/>
\end
\define new-tiddler-temp()
$:/temp/new-tiddler-from-search
\end
\define new-tiddler-from-search-template()
<$button tooltip={{$:/language/Buttons/Clear/Caption}} >
  <$action-deletetiddler $tiddler=<<new-tiddler-temp>>/>
  <$transclude tiddler="$:/core/images/cancel-button" />
</$button><!--
--><$button tooltip={{$:/language/Buttons/NewTiddler/Caption}} actions=<<createTiddler>> >
  <$transclude tiddler="$:/core/images/done-button" /> : <$transclude tiddler="$(searchTiddler)$" />
</$button>
<p>
<$edit-text field="tags" class="wikilabs-full-width" tiddler=<<new-tiddler-temp>> placeholder={{$:/language/EditTemplate/Tags/Add/Placeholder}} /><br>
<$edit-text field="text" class="wikilabs-full-width" tiddler=<<new-tiddler-temp>> placeholder={{$:/language/EditTemplate/Body/Placeholder}} default=""/>
</p>
\end
<<new-tiddler-from-search-template>>"
        },
        "$:/plugins/wikilabs/new-tiddler-from-search/history": {
            "title": "$:/plugins/wikilabs/new-tiddler-from-search/history",
            "text": "V 0.1.2 - 2020.01.19

* Update license link/text

V 0.1.1 - 2016

* initial release
"
        },
        "$:/plugins/wikilabs/new-tiddler-from-search/license": {
            "title": "$:/plugins/wikilabs/new-tiddler-from-search/license",
            "text": "[[New-Tiddler-From-Search-Plugin|https://wikilabs.github.io/#new-tiddler-from-search]] (c) Mario Pietsch - 2016-2020

https://opensource.org/licenses/BSD-3-Clause
"
        },
        "$:/plugins/wikilabs/new-tiddler-from-search/readme": {
            "title": "$:/plugins/wikilabs/new-tiddler-from-search/readme",
            "text": "This plugin provides a minimal "new Tiddler" interface, if a search doesn't return any results. There are 2 buttons.

* {{$:/core/images/cancel-button}} .. Clear the temporary template tiddler
* {{$:/core/images/done-button}} .. Save the tiddler

''Important'': This plugin overwrites core template tiddlers:

* $:/core/ui/DefaultSearchResultList
"
        }
    }
}
